package week1.day2;

public class FindOddNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int OddnumbersvalueRange=20;
		
		
		for (int i=0 ; i<=OddnumbersvalueRange;i++) {
			
			if(i%2==1) {
				System.out.println("The odd number is : " +i);
				
			}
			
		}
			
	}

}
